    constructor(datashare, fb, authService, cs, router, productService) {
        this.datashare = datashare;
        this.fb = fb;
        this.authService = authService;
        this.cs = cs;
        this.router = router;
        this.productService = productService;
        this.customer = { customerId: 0, firstName: '', lastName: '', userName: '', email: '' };
