        //await this.deleteAllProds();
        //console.log("Fired");
        await this.authService.delete(this.customer.customerId).subscribe(data => {
            console.log(data);
            this.datashare.setCustomerData(this.customer = { customerId: 0, firstName: '', lastName: '', userName: '', email: '' });
            this.datashare.setLoginData(false);
        });
        this.router.navigate(['/home']);
    }
    async updateData() {
    }
}
