import { Customer } from '../models/customer.model';
import { BehaviorSubject } from 'rxjs';
import { CartProduct } from '../models/cart_product.model';
import { Product } from '../models/product.model';
import * as i0 from "@angular/core";
export class DatashareService {
    constructor() {
        //MUST BE CLASSES NOT IMPORTS!!!
        this.customerData = new BehaviorSubject(new Customer);
        this.logginData = new BehaviorSubject(false);
        //private cartData: BehaviorSubject<Array<CartProduct>> = new BehaviorSubject([]);
        this.cartData = new BehaviorSubject([new CartProduct]);
        this.totalItems = new BehaviorSubject(0);
        //private cartData = new BehaviorSubject<CartProduct[]>([])
        this.products = new BehaviorSubject([new Product]);
        //{productId: 0, name: '', price: 0, info : '', category : '', picture : '', quantity: 0}
        this.count1 = 0;
        //count2 = 0;
        //item = {cartProductId: 0, productId: 0, customerId: 0, quantity: 0}
        this.currentCustomerData = this.customerData.asObservable();
        this.currentLogginData = this.logginData.asObservable();
        this.currentCartData = this.cartData.asObservable();
        this.currentTotalItems = this.totalItems.asObservable();
        this.currentProducts = this.products.asObservable();
    }
    ngOnInit() {
        this.getTotal();
    }
    setProducts(data) {
        this.products.next(data);
    }
    getProducts() {
        return this.products;
    }
    setCustomerData(data) {
        this.customerData.next(data);
    }
    getCustomerData() {
        return this.customerData;
    }
    setLoginData(data) {
        this.logginData.next(data);
    }
    getLoginData() {
        return this.customerData;
    }
    setCartData(data) {
        this.cartData.next(data);
    }
    getCartData() {
        return this.cartData;
    }
    setTotalItems(data) {
        this.totalItems.next(data);
    }
    getTotalItems() {
        return this.totalItems;
    }
    addCount(value) {
        this.count1 += value;
        this.totalItems.next(this.count1);
    }
    decCount(value) {
        this.count1 -= value;
        this.totalItems.next(this.count1);
    }
    async getTotal() {
        await this.currentCartData.subscribe(data => {
            let sum = 0;
            data.forEach((obj) => {
                sum += obj.quantity;
            });
            this.count1 = sum;
            this.setTotalItems(sum);
        });
    }
}
