import * as i0 from "@angular/core";
import * as i1 from "../services/datashare.service";
import * as i2 from "@angular/forms";
import * as i3 from "../services/auth.service";
import * as i4 from "../services/cart.service";
import * as i5 from "@angular/router";
export class AccountComponent {
    constructor(datashare, fb, authService, cs, router) {
        this.datashare = datashare;
        this.fb = fb;
        this.authService = authService;
        this.cs = cs;
        this.router = router;
        this.customer = { customerId: 0, firstName: '', lastName: '', userName: '', email: '' };
        this.cartData = new Array();
        this.updateForm = this.fb.group({
            'FirstName': [''],
            'LastName': [''],
            'UserName': [''],
            'Email': [''],
            'Password': ['']
        });
    }
    async ngOnInit() {
        this.getCustomer();
        this.getCart();
    }
    async getCustomer() {
        await this.datashare.getCustomerData().subscribe(data => {
            this.customer = data;
        });
    }
    async getCart() {
        await this.datashare.getCartData().subscribe(data => {
            this.cartData = data;
        });
    }
    update() {
        this.authService.update(this.customer.customerId, this.updateForm.value).subscribe(data => {
            this.datashare.setCustomerData(data),
                console.log(data);
        });
    }
    async deleteAllProds() {
        for (let i = 0; i < this.cartData.length; i++) {
            await this.cs.deleteCustomersCart(this.cartData[i].cartProductId).subscribe(data => {
                console.log(data, "all products deleted");
            });
        }
        await this.datashare.setCartData(new Array());
    }
    deleteCustomer() {
        //await this.deleteAllProds();
        //console.log("Fired");
        this.authService.delete(this.customer.customerId).subscribe(data => {
            console.log(data);
            this.datashare.setCustomerData(this.customer = { customerId: 0, firstName: '', lastName: '', userName: '', email: '' });
            this.datashare.setLoginData(false);
        });
        this.router.navigate(['/home']);
    }
}
