        this.fb = fb;
        this.authService = authService;
        this.customer = { customerId: 0, firstName: '', lastName: '', userName: '', email: '' };
        this.updateForm = this.fb.group({
            'FirstName': [''],
            'LastName': [''],
            'UserName': [''],
            'Email': [''],
            'Password': ['']
        });
    }
    async ngOnInit() {
        this.getCustomer();
    }
    async getCustomer() {
        await this.datashare.getCustomerData().subscribe(data => {
            this.customer = data;
        });
    }
    update() {
        this.authService.update(this.customer.customerId, this.updateForm.value).subscribe(data => {
            this.datashare.setCustomerData(data),
                console.log(data);
        });
    }
}
