import { __decorate } from "tslib";
import { Component } from '@angular/core';
let NavMenuComponent = class NavMenuComponent {
    constructor(datashare, cartService, ps, router, fb) {
        this.datashare = datashare;
        this.cartService = cartService;
        this.ps = ps;
        this.router = router;
        this.fb = fb;
        this.isLoggedinData = false;
        this.totalItems = 0;
        this.products = [];
        this.searchText = '';
        this.getCount();
        //this.getProds();
        this.searchForm = this.fb.group({
            'SearchTerm': this.searchText
        });
    }
    ngOnInit() {
        this.isLoggedIn();
        this.getAllProducts();
    }
    getCount() {
        this.datashare.currentTotalItems.subscribe(data => {
            this.totalItems = data;
        });
    }
    async isLoggedIn() {
        await this.datashare.currentLogginData.subscribe(data => {
            this.isLoggedinData = data;
        });
    }
    search(s) {
        this.searchText = s;
        this.datashare.setSearch(s);
        this.router.navigate(['/products']);
    }
    getAllProducts() {
        this.datashare.getProducts().subscribe(response => {
            this.products = response;
            console.log(response);
        });
    }
};
NavMenuComponent = __decorate([
    Component({
        selector: 'app-nav-menu',
        templateUrl: './nav-menu.component.html',
        styleUrls: ['./nav-menu.component.css']
    })
], NavMenuComponent);
export { NavMenuComponent };
