    constructor(datashare, productService, cs) {
        this.datashare = datashare;
        this.productService = productService;
        this.cs = cs;
        this.cartData = [];
        this.products = [];
        this.totalItems = 0;
        this.totalCost = 0;
        this.deleteID = 0;
        this.index = 0;
        this.arrayElements = [1, 2, 3, 4];
        this.getCount();
        this.getCart();
        this.getInfo();
    }
    ngOnInit() {
    }
    async getInfo() {
        this.products = [];
        this.totalCost = 0;
        for (let i = 0; i < this.cartData.length; i++) {
            //let total = 0;
            let idNumb = this.cartData[i].productId;
            let p = { product: { productId: 0, name: '', price: 0, info: '', category: '', picture: '', quantity: 0 }, quantityCart: 0, subTotal: 0.00, cpId: 0 };
