                await this.getProduct(idNumb).subscribe(data => {
                    p.product = data;
                    p.cpId = this.cartData[i].cartProductId;
                    p.quantityCart = this.cartData[i].quantity;
                    p.subTotal = parseFloat((this.cartData[i].quantity * data.price).toFixed(2));
                    this.totalCost += parseFloat((this.cartData[i].quantity * data.price).toFixed(2));
                    this.products.push(p);
                });
            }
        }
        this.totalCost = parseFloat(this.totalCost.toFixed(2));
    }
    getProduct(id) {
        return this.productService.getProd(id);
    }
    async deleteProd() {
        let id = 0;
        await this.cs.deleteCustomersCart(this.deleteID).subscribe(data => {
            //console.log("deleted");
        });
        //this.removeItem(this.deleteID);
    }
    setID(i) {
        this.deleteID = i;
    }
    setIndex(i) {
        this.index = i;
    }
    async updateData() {
        // let p1 = this.products[this.index];
        let p2 = this.products[this.index].product;
        p2.quantity = p2.quantity + this.cartData[this.index].quantity;
        console.log(p2);
        await this.productService.updateProd(p2.productId, p2).subscribe(data => {
            //this.datashare.setCustomerData(data),
            console.log(data);
        });
    }
    getCount() {
        this.datashare.currentTotalItems.subscribe(data => {
            this.totalItems = data;
        });
    }
    getCart() {
        this.datashare.currentCartData.subscribe(data => {
            this.cartData = data;
            console.log(data);
        });
    }
    removeItem(i) {
        delete this.cartData[i];
        const btn = document.getElementById('btn');
        btn.addEventListener('click', () => {
            // üëáÔ∏è hide button
            btn.style.display = 'none';
            // üëáÔ∏è show div
            const box = document.getElementById('box');
            box.style.display = 'block';
        });
    }
}
